CREATE TABLE IF NOT EXISTS authors (
  id_author BIGINT NOT NULL,
  name VARCHAR(50) NOT NULL,
  nickname VARCHAR(50) NOT NULL,
  image VARCHAR(50) NULL,
  PRIMARY KEY (id_author));
  
  INSERT INTO authors (id_author, name, nickname, image) VALUES (1, '√Ålvaro Fuentes Pastor', 'AFuentes', 'author-1.png');


CREATE TABLE IF NOT EXISTS posts (
  id_post BIGINT NOT NULL,
  post_date TIMESTAMP NOT NULL,
  post_update_date TIMESTAMP NOT NULL,
  image VARCHAR(300) NULL,
  id_author BIGINT NOT NULL,
  PRIMARY KEY (id_post));

  INSERT INTO posts (id_post, post_date, post_update_date, image, id_author) VALUES (1, CURRENT_TIMESTAMP() - 10, CURRENT_TIMESTAMP() - 10, '1.jpg', 1);
  INSERT INTO posts (id_post, post_date, post_update_date, image, id_author) VALUES (2, CURRENT_TIMESTAMP() - 5, CURRENT_TIMESTAMP() - 5, '2.jpg', 1);
  INSERT INTO posts (id_post, post_date, post_update_date, image, id_author) VALUES (3, CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP(), '3.jpg', 1);

DROP TABLE IF EXISTS posts_languages;
CREATE TABLE IF NOT EXISTS posts_languages (
  id_post_language BIGINT NOT NULL,
  id_post BIGINT NOT NULL,
  language VARCHAR(2) NOT NULL,
  title VARCHAR(100) NOT NULL,
  content TEXT NOT NULL,
  summary VARCHAR(500) NOT NULL,
  url VARCHAR(100) NOT NULL,
  PRIMARY KEY (id_post_language));
  
