DROP TABLE IF EXISTS posts_comments;
CREATE TABLE IF NOT EXISTS posts_comments (
  id_comment BIGINT NOT NULL,
  id_post BIGINT NOT NULL,
  name VARCHAR(50) NOT NULL,
  comment TEXT NOT NULL,
  image VARCHAR(300) NULL,
  PRIMARY KEY (id_comment));
  
  INSERT INTO posts_comments (id_comment, id_post, name, comment, image) VALUES (1, 1, 'Ezio', 'I do this not for myself. Compio questo sacrificio per il bene superiore (I make this sacrifice for the greater good). Requiescant in pace (Rest in peace).', 'commenter-1.jpg');
  INSERT INTO posts_comments (id_comment, id_post, name, comment, image) VALUES (2, 1, 'Altair', 'Nothing is true; everything is permitted.', 'commenter-2.jpg');